import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  plugins: {
    title: {
      display: true,
      text: 'LeetCode Category Summary',
    },
  },
  responsive: true,
  scales: {
    x: {
      stacked: true,
    },
    y: {
      stacked: true,
    },
  },
};

const labels = ['Arrays & Hashing', 'Two Pointers', 'Sliding Window', 'Stack', 'Binary Search', 'Linked List', 'Trees'];

export const data = {
  labels,
  datasets: [
    {
      label: 'Easy',
      data: labels.map(() => 10),
      backgroundColor: 'rgb(0, 255, 136)',
    },
    {
      label: 'Medium',
      data: labels.map(() => 20),
      backgroundColor: 'rgb(231, 168, 50)',
    },
    {
      label: 'Hard',
      data: labels.map(() => 15),
      backgroundColor: 'red',
    },
  ],
};

export function Dashboard() {
  // Fields
  const [listOfProblems, setListOfProblems] = useState([])
  const [name, setName] = useState("")
  const [difficulty, setDifficulty] = useState("")
  var curr = new Date();
  curr.setDate(curr.getDate() + 3);
  var defaultDate = curr.toISOString().substring(0,10);
  const [date, setDate] = useState(defaultDate)
  const [duration, setDuration] = useState(0)
  const [attempts, setAttempts] = useState(0)
  const [completed, setCompleted] = useState("")  

  // retrieve data from MongoDB
  useEffect(() => {
    Axios.get("http://localhost:3001/getProblems").then((response) => {
      setListOfProblems(response.data)
    });
  }, [])

  // functions
  const createProblem = () => {
    Axios.post("http://localhost:3001/createProblem", {
      name, 
      difficulty, 
      date,
      duration,
      attempts,
      completed,
    }).then((response) => {
      setListOfProblems([...listOfProblems, {
        name, 
        difficulty, 
        date,
        duration,
        attempts,
        completed,
      }])
    })
  }
  return <div id='chart'><Bar options={options} data={data} /></div>;
}

export default Dashboard;
