import './App.css';
import { useState, useEffect } from "react";
import Axios from "axios";
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'
import Dashboard from './Pages/Dashboard';
import Home from './Pages/Home';
import Submissions from './Pages/Submissions';

function App() {

  // Fields
  const [listOfProblems, setListOfProblems] = useState([])
  const [name, setName] = useState("")
  const [difficulty, setDifficulty] = useState("")
  var curr = new Date();
  curr.setDate(curr.getDate() + 3);
  var defaultDate = curr.toISOString().substring(0,10);
  const [date, setDate] = useState(defaultDate)
  const [duration, setDuration] = useState(0)
  const [attempts, setAttempts] = useState(0)
  const [completed, setCompleted] = useState("")  

  // retrieve data from MongoDB
  useEffect(() => {
    Axios.get("http://localhost:3001/getProblems").then((response) => {
      setListOfProblems(response.data)
    });
  }, [])

  // functions
  const createProblem = () => {
    Axios.post("http://localhost:3001/createProblem", {
      name, 
      difficulty, 
      date,
      duration,
      attempts,
      completed,
    }).then((response) => {
      setListOfProblems([...listOfProblems, {
        name, 
        difficulty, 
        date,
        duration,
        attempts,
        completed,
      }])
    })
  }

  // rendering
  return (
    <Router>
      <Routes>
        <Route path='/' element={<Home />} />
        <Route path='/dashboard' element={<Dashboard />} />
        <Route path='/submissions' element={<Submissions />} />
      </Routes>
